// 389. Find the Difference
//
// Given two strings s and t which consist of only lowercase letters.
//
// String t is generated by random shuffling string s and then add one more letter at a random position.
//
// Find the letter that was added in t.
//
// Example:
//
// Input:
// s = "abcd"
// t = "abcde"
//
// Output:
// e
//
// Explanation:
// 'e' is the letter that was added.

// The case is not as simple as the example so the one below will have wrong answers.
// Eg. ""
//  ""
class Solution {
public:
    char findTheDifference(string s, string t) {
        int len = s.size();
        for (int i = 0; i < len; i++) {
            if (s[i] != t[i]) return t[i];
        }
        return t[len];
    }
};

// 4 ms, faster than 87.81%
class Solution {
public:
  char findTheDifference(string s, string t) {
    int j = 0;
    int lens = s.size();
    int lent = t.size();
    for (int i = 0; i < lens; i++) {
      j = j ^ int(s[i]); // exclusive or ^
    }
    for (int i = 0; i < lent; i++) {
      j = j ^ int(t[i]); // if the character is the same as string s, the contribution of that character will become 0
    }
    return char(j);
  }
};